import "../styles/Board.css";
import React, { useEffect, useRef} from 'react';
import { useLocation } from 'react-router-dom';
import scarlet from "../images/scarlet.PNG";
import green from "../images/green.PNG";
import mustard from "../images/mustard.PNG";
import white from "../images/white.PNG";
import plum from "../images/plum.PNG";
import peacock from "../images/peacock.PNG";

import io from 'socket.io-client'
const socket = io.connect('http://localhost:4000');

export default function Board(props) {
    const room = useRef("");
    const count = useRef(0);
    const characterRef = useRef(null);
    const locationIndex = useRef();

    const { state: characterFromSelection } = useLocation();

    useEffect(() => {
        if (characterFromSelection && count.current === 0) {
            let rooms = ["study11", "hall13", "lounge15", "library31", "billiard33", "dinning35", "conservatory51", "ballroom53", "kitchen55"];
            const roomPositionIndex = Math.floor(Math.random() * rooms.length);
            const roomPosition = rooms[roomPositionIndex];

            locationIndex.current = parseInt(roomPosition.substring(roomPosition.length - 2));

            let imgSrc;
            [scarlet, green, mustard, white, plum, peacock].forEach((el) => {
                if (el.includes(characterFromSelection.character)) {
                    imgSrc = el;
                }
            });

            const img = document.createElement("img");
            img.src = imgSrc;
            img.style.height = "50%";
            img.style.width = "50%";
            img.alt = characterFromSelection.character;
            img.onclick = characterSelected;

            document.getElementById(roomPosition).appendChild(img);

            socket.emit("character_map", { "on_map": [roomPosition, characterFromSelection.character, imgSrc] });

            count.current++;

            // Cleanup function to remove the image when the component unmounts
            return () => {
                document.getElementById(roomPosition).removeChild(img);
            };
        }
    }, [characterFromSelection]);

    useEffect(() => {
        socket.on("receive_map", (data) => {
            if (count.current === 0) {
                const img = document.createElement("img");
                img.src = data.on_map[2];
                img.style.height = "50%";
                img.style.width = "50%";
                img.alt = data.on_map[1];
                img.onclick = characterSelected;

                document.getElementById(data.on_map[0]).appendChild(img);

                count.current++;
            }
        });

        // Cleanup function to remove event listener when component unmounts
        return () => {
            socket.off("receive_map");
        };
    }, []);

    function characterSelected(event) {
        if (event.target.style.border === "2px solid red") {
            event.target.style.border = "";
            characterRef.current = "";
        } else {
            event.target.style.border = "2px solid red";
            characterRef.current = event.target;
        }
    }

    function characterMovement(event) {
        if (characterRef.current) {
            const nextLocation = document.getElementById(event.target.id);
            const tempIndex = parseInt(event.target.id.substring(event.target.id.length - 2));

            if ([locationIndex.current + 1, locationIndex.current - 1, locationIndex.current + 10, locationIndex.current - 10].includes(tempIndex)) {
                nextLocation.appendChild(characterRef.current);
                locationIndex.current = tempIndex;
            }
        }
    }

    return (
        <div className="container">
            <div className="row">
                <div className="left-box col-8 d-flex justify-content-center align-items-center shadow-lg">
                    <div className="game-board">
                        <div className="row ms-2">
                            <div className="col study" id="study11" onClick={characterMovement}></div>
                            <div className="col hallway" id="hallway12" onClick={characterMovement}></div>
                            <div className="col hall" id="hall13" onClick={characterMovement}></div>
                            <div className="col hallway" id="hallway14" onClick={characterMovement}></div>
                            <div className="col lounge" id="lounge15" onClick={characterMovement}></div>
                        </div>

                        <div className="row ms-2">
                            <div className="col hallway-vertical" id="hallway21" onClick={characterMovement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway23" onClick={characterMovement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway25" onClick={characterMovement}></div>
                        </div>

                        <div className="row ms-1">
                            <div className="col library" id="library31" onClick={characterMovement}></div>
                            <div className="col hallway" id="hallway32" onClick={characterMovement}></div>
                            <div className="col billiard" id ="billiard33" onClick={characterMovement}></div>
                            <div className="col hallway" id="hallway34" onClick={characterMovement}></div>
                            <div className="col dinning" id="dinning35" onClick={characterMovement}></div>
                        </div>

                        <div className="row ms-1">
                            <div className="col hallway-vertical" id="hallway41" onClick={characterMovement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway43" onClick={characterMovement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway45" onClick={characterMovement}></div>
                        </div>

                        <div className="row ms-1">
                            <div className="col conservatory" id="conservatory51" onClick={characterMovement}></div>
                            <div className="col hallway" id="hallway52" onClick={characterMovement}></div>
                            <div className="col ballroom" id="ballroom53" onClick={characterMovement}></div>
                            <div className="col hallway" id="hallway54" onClick={characterMovement}></div>
                            <div className="col kitchen" id="kitchen55" onClick={characterMovement}></div>
                        </div>
                    </div>
                </div>
                <div className="col-4 note"></div>
            </div>
        </div>
    );
}
