import { useNavigate } from "react-router-dom"
import '../styles/join.css'
import { useState } from "react";

import React from 'react';
import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

export default function Join(){

    const [code, setCode] = useState();
    const [holder, setHolder] = useState();

    const navigate = useNavigate();

    function characterSelection(){
        navigate('/character')
    }

    function codeGenerate(){

        return (

            <Popup trigger={<button> Trigger</button>} position="right center">
                <div>Popup content here !!</div>
            </Popup>

        )



    }

    function roomJoin(){
        setCode(Math.floor(Math.random() * 9000) + 1000);

    }




    return(
        <div className="container-fluid position-relative text-center bg-join">

             <div className="position-absolute top-50 start-50 translate-middle">
                <h1 className="mb-5">Select a Game Type</h1>
                <div className="d-flex justify-content-center align-items-center ">
                    <form>
                        <div class="form-floating">
                            <input type="text" className="form-control" id="floatingInput" placeholder="" />
                            <label for="floatingInput">Player Name</label>
                        </div>
                        <div class="my-3">
                            <div class="form-check">
                                <Popup trigger={
                                        <input onChange={codeGenerate} id="debit" name="paymentMethod" type="radio" class="form-check-input" required="" />
                                    } position="right center">

                                        <div>Popup content here !!</div>

                                </Popup>

                                <label class="create-room form-check-label" for="debit">Create a Room</label>
                            </div>
                            <div class="form-check">
                                <input id="paypal" onChange={roomJoin} name="paymentMethod" type="radio" class="form-check-input" required="" />
                                <label class="join-room form-check-label" for="paypal">Join a Room</label>
                            </div>
                        </div>

                        <div className="play-type" id="play-type">
                            <input type="text" className="form-control" id="create-code floatingInput" value={code} placeholder=""/>
                        </div>

                    </form>


                </div>

             </div>

             <div className="fixed-bottom mb-5 pb-5">
                <button onClick={characterSelection} type="button" className="continue-button mt-3"><p className="continue-text">Continue</p></button>
             </div>


            <div className="fixed-bottom">
                <footer className="text-body-secondary text-center text-small mt-md-5">
                    <p className="foot-info mb-1">© 2023–2024 GameJHU</p>
                </footer>
            </div>
        </div>


    )

}
