import "../styles/Board.css";
import React, { useEffect, useRef, useState} from 'react';
import scarlet from "../images/scarlet.PNG"; // Import the image URL

export default function Board() {
    const room = useRef("");
    const count = useRef(0);

     const characterRef = useRef(null);

    useEffect(() => {
        function initialization() {
            const rooms = ["study", "hall", "lounge", "library", 
                           "billiard", "dinning", "conservatory", "ballroom", "kitchen"];

            const room_position = rooms[Math.floor(Math.random() * 9)];

            if (room.current === "") {
                room.current = room_position;
            }

            if (count.current === 0){
                // Check if there's already an image in the room
                const img = document.createElement("img");
                img.src = scarlet; 
                img.style.height = "50%";
                img.style.width = "50%";
                img.alt = "scarlet";
                img.onclick = character_selected;
                document.querySelector(`.${room.current}`).appendChild(img);

                count.current = 1;

            }

        }
        initialization();
    }, []);

    function character_selected(event) {
        // Logic to handle the selection of the character
        // For example, you can change the border color of the selected character
        event.target.style.border = "2px solid red";
        // Store the reference to the selected character
        characterRef.current = event.target;
    }

    function character_movement(event) {
        const temp = event.target.className;
        // Check if the scarlet image has been selected
        if (characterRef.current && event.target) {
            // Move the scarlet image to the hallway
            console.log(event.target.className)
            const hallway = document.querySelector(`.${temp}`);
            hallway.appendChild(characterRef.current);
        }
    }


    return (
        <div className="container">
            <div className="left-box col-8 d-flex justify-content-center align-items-center shadow-lg">
                <div className="game-board">
                    <div className="row ms-2">
                        <div className="col study"></div>
                        <div className="col hallway12" onClick={character_movement}></div>
                        <div className="col hall"></div>
                        <div className="col hallway"></div>
                        <div className="col lounge"></div>
                    </div>

                    <div className="row ms-2">
                        <div className="col hallway-vertical"></div>
                        <div className="col"></div>
                        <div className="col hallway-vertical"></div>
                        <div className="col"></div>
                        <div className="col hallway-vertical"></div>
                    </div>

                    <div className="row ms-1">
                        <div className="col library"></div>
                        <div className="col hallway"></div>
                        <div className="col billiard"></div>
                        <div className="col hallway"></div>
                        <div className="col dinning"></div>
                    </div>

                    <div className="row ms-1">
                        <div className="col hallway-vertical"></div>
                        <div className="col"></div>
                        <div className="col hallway-vertical"></div>
                        <div className="col"></div>
                        <div className="col hallway-vertical"></div>
                    </div>

                    <div className="row ms-1">
                        <div className="col conservatory"></div>
                        <div className="col hallway"></div>
                        <div className="col ballroom"></div>
                        <div className="col hallway"></div>
                        <div className="col kitchen"></div>
                    </div>
                </div>
            </div>
        </div>
    );
}



