import "../styles/Board.css"
// import study from "../images/study.png"
// import hall from "..//images/hall.png"
// import lounge from "..//images/lounge.png"
// import library from "..//images/library.png"
// import billiard from "..//images/billiard.png"
// import dinning from "..//images/dinning.png"
// import conservatory from "..//images/conservatory.png"
// import ballroom from "..//images/ballroom.png"
// import kitchen from "..//images/kitchen.png"
import React from 'react'; // Import React
import scarlet from "../images/scarlet.PNG"; // Import the image URL

export default function Board() {

    function getPosition(event){
        // Get the x and y coordinates of the click relative to the container
        const x = event.nativeEvent.offsetX;
        const y = event.nativeEvent.offsetY;

        // Log or use the x and y coordinates as needed
        alert(`X: ${x}, Y: ${y}`);
    }

    function initializedCharacters() {

        const locations = {
            study: [[75, 160], [82, 118]],
            hall: [[80, 460], [50, 252]],
            lounge: [[621, 683], [44, 210]],
            library: [[76, 175], [284, 339]],
            billiard: [[77, 140], [441, 509]],
            dinning: [[57, 181], [316, 490]],
            conservatory: [[40, 197], [610, 770]],
            ballroom: [[323, 444], [606, 676]],
            kitchen: [[636, 709], [631, 695]]
        };

        const randomIndex = Math.floor(Math.random() * Object.keys(locations).length); // Generate a random index
        const randomRoom = Object.keys(locations)[randomIndex]; // Get a random room name

        // Generate random x and y coordinates within the range of the selected room
        const x = Math.floor(Math.random() * (locations[randomRoom][0][1] - locations[randomRoom][0][0]) + locations[randomRoom][0][0]);
        const y = Math.floor(Math.random() * (locations[randomRoom][1][1] - locations[randomRoom][1][0]) + locations[randomRoom][1][0]);


        // Return the JSX for the initialized character image
        return (
            <img 
                src={scarlet} // Set the source of the image
                alt="character" // Set the alt text
                style={{
                    position: 'absolute', // Set the position to absolute
                    left: `${x}px`, // Set the left position based on x
                    top: `${y}px`, // Set the top position based on y
                    height:'80px',
                    width:'80px',
                }}
            />
        );
    }

    return (
        <div className="container">
            <div className="row vh-100">
                <div className="board col-7 p-4 p-md-5 mb-4 rounded bg-body-secondary shadow-sm" onClick={getPosition}>


                    {initializedCharacters()} {/* Call initializedCharacters function */}
                </div>
                <div className="col-5 d-flex flex-column">
                    <div className="vh-70 bg-image position-relative rounded shadow-sm mb-2">
                        <div className="row position-absolute start-50 top-50 translate-middle">
                            <div className="col">
                                Suspect
                            </div>
                        </div>
                    </div>
                    <div className="vh-30 rounded shadow-sm">chat box</div>
                </div>
            </div>
        </div>
    );
}
