export default function Board(props) {
    const { state: character_from_selection } = useLocation();

    useEffect(() => {
        if (character_from_selection) {
            let rooms = ["study11", "hall13", "lounge15", "library31", "billiard33", "dinning35", "conservatory51", "ballroom53", "kitchen55"];
            let img_src;
            [scarlet, green, mustard, white, plum, peacock].forEach((el) => {
                if (el.includes(character_from_selection.character)) {
                    img_src = el;
                }
            });

            const img = new Image();
            img.src = img_src;
            img.style.height = "8%";
            img.style.width = "5%";

            // Calculate the center coordinates of the board
            const board = document.getElementById("clue-board");
            const boardRect = board.getBoundingClientRect();
            const centerX = boardRect.width / 2 - img.width / 2;
            const centerY = boardRect.height / 2 - img.height / 2;

            // Set the position of the image to the center of the board
            img.style.position = "absolute";
            img.style.top = centerY + "px";
            img.style.left = centerX + "px"; 

            img.alt = character_from_selection.character;
            img.id = character_from_selection.character;

            board.appendChild(img);
        }
    }, [character_from_selection]);

    function getPosition(event){
        const rect = event.target.getBoundingClientRect();
        const offsetX = event.clientX - rect.left;
        const offsetY = event.clientY - rect.top;

        console.log(offsetX);
        console.log(offsetY);
    }

    return (
        <div className="container-fluid">
            <div className="board row">
                <div className="clue-board col-8 shadow-sm" id="clue-board" onClick={getPosition}>
                    {/* Image will be appended here */}
                </div>
                <div className="col-4 shadow-sm">note</div>
            </div>
        </div>
    );
}
