import "../styles/Board.css";
import React, { useEffect, useRef} from 'react';
import { useLocation } from 'react-router-dom';
import scarlet from "../images/scarlet.PNG";
import green from "../images/green.PNG";
import mustard from "../images/mustard.PNG";
import white from "../images/white.PNG";
import plum from "../images/plum.PNG";
import peacock from "../images/peacock.PNG";

import io from 'socket.io-client'
const socket = io.connect('http://localhost:4000');


export default function Board(props) {
    const room = useRef("");
    const count = useRef(0);

    const characterRef = useRef(null);

    const location_index = useRef();

    const { state: character_from_selection } = useLocation();



    useEffect(() => {
        if (character_from_selection) {
            console.log(character_from_selection.character);

            if (count.current === 0) {
                let rooms = ["study11", "hall13", "lounge15", "library31", 
                "billiard33", "dinning35", "conservatory51", "ballroom53", "kitchen55"];

                for (let i = 0; i < character_from_selection.character.length; i++) {
                    const room_position_index = Math.floor(Math.random() * rooms.length);
                    const room_position = rooms[room_position_index];

                    room.current = room_position;    
                    rooms.splice(room_position_index, 1);

                    // Check if there's already an image in the room
                    let img_src; 
                    [scarlet, green, mustard, white, plum, peacock].forEach((el) => {
                        if (el.includes(character_from_selection.character[i])) {
                            img_src = el;
                        }
                    });

                    const img = document.createElement("img");
                    img.src = img_src; 
                    img.style.height = "50%";
                    img.style.width = "50%";
                    img.alt = character_from_selection.character[i]; 
                    img.onclick = character_selected;
                    location_index.current = parseInt(room.current.substring(room.current.length - 2));
                    document.getElementById(room.current).appendChild(img);

                    socket.emit("character_map", {"on_map":[room.current, character_from_selection.character[i], img.src]});


                }
                count.current = 1;
            }
        }
    }, [character_from_selection]);


    useEffect(() => {
        socket.on("receive_map", (data) => {

            console.log(data.on_map[0])
            console.log(data.on_map[1])

            const img = document.createElement("img");
            img.src = img_src; 
            img.style.height = "50%";
            img.style.width = "50%";
            img.alt = character_from_selection.character[i]; 
            img.onclick = character_selected;
            location_index.current = parseInt(room.current.substring(room.current.length - 2));
            document.getElementById(room.current).appendChild(img);

            socket.emit("character_map", {"on_map":[room.current, character_from_selection.character[i]]});


            // document.getElementById(data.on_map[0]).appendChild(data.on_map[1]);
        });

        // Cleanup function to remove event listener when component unmounts
        return () => {
            socket.off("receive_map");
        };
    }, []);




    // useEffect(() => {
    //     function initialization() {
    //         let rooms = ["study11", "hall13", "lounge15", "library31", 
    //                        "billiard33", "dinning35", "conservatory51", "ballroom53", "kitchen55"];

    //         for(let i = 0; i < rooms.length; i ++) {
    //             const room_position_index = Math.floor(Math.random() * rooms.length);
    //             const room_position = rooms[room_position_index];


    //             room.current = room_position;


    //             rooms.splice(room_position_index, 1);


    //             // Check if there's already an image in the room
    //             let img_src; 
    //             [scarlet, green, mustard, white, plum, peacock].forEach((el) => {
    //                 if (el.includes(character_from_selection.character[i])) {
    //                     img_src = el;
    //                 }
    //             });

    //             const img = document.createElement("img");
    //             img.src = img_src; 
    //             img.style.height = "50%";
    //             img.style.width = "50%";
    //             img.alt = character_from_selection.character[i]; 
    //             img.onclick = character_selected;
    //             location_index.current = parseInt(room.current.substring(room.current.length - 2));
    //             document.getElementById(room.current).appendChild(img);

    //             count.current = 1;

    //         }
    //     }
    //     initialization();
    // }, [character_from_selection]);


    function character_selected(event) {
        // Logic to handle the selection of the character
        // For example, you can change the border color of the selected character
        if (event.target.style.border === "2px solid red"){
            event.target.style.border = "";
            characterRef.current = "";

        }else{
            event.target.style.border = "2px solid red";      
            // Store the reference to the selected character
            characterRef.current = event.target;
        }

    }

    function character_movement(event) {
        // Check if the scarlet image has been selected and if event.target exists
        if (characterRef.current && event.target) {
            // Move the scarlet image to the clicked hallway
            const location = document.getElementById(event.target.id);

            if (location) {
                const temp_index = parseInt(location.id.substring(location.id.length-2));
                if([location_index.current + 1, location_index.current - 1, location_index.current + 10, location_index.current - 10].includes(temp_index)){
                    location.appendChild(characterRef.current);
                    location_index.current = temp_index;

                }


            }
        }
    }


    return (
        <div className="container">
            <div className="row">
                <div className="left-box col-8 d-flex justify-content-center align-items-center shadow-lg">
                    <div className="game-board">
                        <div className="row ms-2">
                            <div className="col study" id="study11" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway12" onClick={character_movement}></div>
                            <div className="col hall" id="hall13" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway14" onClick={character_movement}></div>
                            <div className="col lounge" id="lounge15" onClick={character_movement}></div>
                        </div>

                        <div className="row ms-2">
                            <div className="col hallway-vertical" id="hallway21" onClick={character_movement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway23" onClick={character_movement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway25" onClick={character_movement}></div>
                        </div>

                        <div className="row ms-1">
                            <div className="col library" id="library31" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway32" onClick={character_movement}></div>
                            <div className="col billiard" id ="billiard33" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway34" onClick={character_movement}></div>
                            <div className="col dinning" id="dinning35" onClick={character_movement}></div>
                        </div>

                        <div className="row ms-1">
                            <div className="col hallway-vertical" id="hallway41" onClick={character_movement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway43" onClick={character_movement}></div>
                            <div className="col"></div>
                            <div className="col hallway-vertical" id="hallway45" onClick={character_movement}></div>
                        </div>

                        <div className="row ms-1">
                            <div className="col conservatory" id="conservatory51" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway52" onClick={character_movement}></div>
                            <div className="col ballroom" id="ballroom53" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway54" onClick={character_movement}></div>
                            <div className="col kitchen" id="kitchen55" onClick={character_movement}></div>
                        </div>
                    </div>
                </div>

            <div className="col-4 note"></div>

            </div>

        </div>
    );
}



