import scarlet from '../images/scarlet.PNG'
import green from '../images/green.PNG'
import mustard from '../images/mustard.PNG'
import white from '../images/white.PNG'
import plum from '../images/plum.PNG'
import peacock from '../images/peacock.PNG'
import {useEffect, useRef} from 'react'
import { useNavigate } from "react-router-dom";


import '../styles/character.css'

import io from 'socket.io-client'
const socket = io.connect('http://localhost:4000');

export default function Character(){

    let character_selected = useRef();

    const navigate = useNavigate();
    function gameBoard(){

        if(character_selected){

            socket.emit("send_start", {"message_start":"let's play"})
            navigate('/board', { state: { character: character_selected.current} });


        }

    }

    useEffect(() => {
        socket.on("receive_start", (data) => {
            navigate('/board');

        })
    })

    function selected(character){

        if(document.getElementById(character).textContent === "Select"){

            document.getElementById(character).textContent = "Selected";
            character_selected.current.push(character);
            socket.emit("send_message", {"message": [character, 'true']});


        }else{

            document.getElementById(character).textContent = "Select";
            const index = character_selected.current.indexOf(character);
            character_selected.current.splice(index, 1);
            socket.emit("send_message", {"message": [character, 'false']});
        }


    }

    useEffect(() => {
        socket.on("receive_message", (data) => {

            if (data.message[1] === 'true'){
                document.getElementById(data.message[0]).innerText = "Selected";
                if (!character_selected.current.includes(data.message[0])){
                    character_selected.current.push(data.message[0])

                }

            }else{
                document.getElementById(data.message[0]).innerText = "Select";
                const index = character_selected.current.indexOf(data.message[0]);
                character_selected.current.splice(index, 1);

            }
        });
    }, []);



    return(
        <div className="container-fluid text-center bg-char">
            <div class="characters row text-center">
                <div class="col-2">
                    <div class="card mb-4 rounded-3 shadow-sm">
                    <div class="card-header py-3">
                        <h4 class="my-0 fw-normal">Scarlet</h4>
                    </div>
                    <div class="card-body">
                        <img className='img-fluid rounded' src={scarlet} alt='scarlet' />
                        <button onClick={() => selected('scarlet')} type="button" class="w-100 btn btn-lg btn-outline-primary" id="scarlet">Select</button>
                    </div>
                    </div>
                </div>

                <div class="col-2">
                    <div class="card mb-4 rounded-3 shadow-sm">
                    <div class="card-header py-3">
                        <h4 class="my-0 fw-normal">Green</h4>
                    </div>
                    <div class="card-body">
                    <img className='img-fluid rounded' src={green} alt='green' />
                        <button onClick={() => selected('green')} type="button" class="w-100 btn btn-lg btn-outline-primary" id="green">Select</button>
                    </div>
                    </div>
                </div>

                <div class="col-2">
                    <div class="card mb-4 rounded-3 shadow-sm">
                    <div class="card-header py-3">
                        <h4 class="my-0 fw-normal">Mustard</h4>
                    </div>
                    <div class="card-body">
                    <img className='img-fluid rounded' src={mustard} alt='mustard' />
                        <button onClick={() => selected('mustard')} type="button" class="w-100 btn btn-lg btn-outline-primary" id="mustard">Select</button>
                    </div>
                    </div>
                </div>

                <div class="col-2">
                    <div class="card mb-4 rounded-3 shadow-sm">
                    <div class="card-header py-3">
                        <h4 class="my-0 fw-normal">Peacock</h4>
                    </div>
                    <div class="card-body">
                    <img className='img-fluid rounded' src={peacock} alt='peacock' />
                        <button onClick={() => selected('peacock')} type="button" class="w-100 btn btn-lg btn-outline-primary" id="peacock">Select</button>
                    </div>
                    </div>
                </div>

                <div class="col-2">
                    <div class="card mb-4 rounded-3 shadow-sm">
                    <div class="card-header py-3">
                        <h4 class="my-0 fw-normal">Plum</h4>
                    </div>
                    <div class="card-body">
                    <img className='img-fluid rounded' src={plum} alt='plum' />
                        <button onClick={() => selected('plum')} type="button" class="w-100 btn btn-lg btn-outline-primary" id="plum">Select</button>
                    </div>
                    </div>
                </div>

                <div class="col-2">
                    <div class="card mb-4 rounded-3 shadow-sm">
                    <div class="card-header py-3">
                        <h4 class="my-0 fw-normal">White</h4>
                    </div>
                    <div class="card-body">
                    <img className='img-fluid rounded' src={white} alt='white' />
                        <button onClick={() => selected('white')} type="button" class="w-100 btn btn-lg btn-outline-primary" id="white">Select</button>
                    </div>
                    </div>
                </div>


            </div>

            <div className="fixed-bottom mb-5 pb-5">
                <button onClick={gameBoard} type="button">Play Game</button>

            </div>

            <div className="fixed-bottom">
                <footer className="text-body-secondary text-center text-small mt-md-5">
                    <p className="mb-1">© 2023–2024 GameJHU</p>
                </footer>
            </div>
        </div>
    )


}
