    //         const img = document.createElement("img");
    //         img.src = data.on_map[2]; 
    //         img.style.height = "50%";
    //         img.style.width = "50%";
    //         img.alt = data.on_map[1]; 
    //         img.onclick = character_selected;
    //         location_index.current = parseInt(data.on_map[0].substring(room.current.length - 2));
    //         document.getElementById(data.on_map[0]).appendChild(img);
    //     };

    //     socket.on("receive_map", receiveMapHandler);

    //     // Cleanup function to remove event listener when component unmounts
    //     return () => {
    //         socket.off("receive_map", receiveMapHandler);
    //     };
    // }, []);



    function character_selected(event) {
        // Logic to handle the selection of the character
        // For example, you can change the border color of the selected character
        if (event.target.style.border === "2px solid red"){
            event.target.style.border = "";
            characterRef.current = "";

        }else{
            event.target.style.border = "2px solid red";      
            // Store the reference to the selected character
            characterRef.current = event.target;
        }

    }

    function character_movement(event) {
        // Check if the scarlet image has been selected and if event.target exists
        if (characterRef.current && event.target) {
            // Move the scarlet image to the clicked hallway
            const location = document.getElementById(event.target.id);

            if (location) {
                const temp_index = parseInt(location.id.substring(location.id.length-2));
                if([location_index.current + 1, location_index.current - 1, location_index.current + 10, location_index.current - 10].includes(temp_index)){
                    location.appendChild(characterRef.current);
                    location_index.current = temp_index;

                }


            }
        }
    }


    return (
        <div className="container">
            <div className="row">
                <div className="left-box col-8 d-flex justify-content-center align-items-center shadow-lg">
                    <div className="game-board">
                        <div className="row ms-2">
                            <div className="col study" id="study11" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway12" onClick={character_movement}></div>
                            <div className="col hall" id="hall13" onClick={character_movement}></div>
                            <div className="col hallway" id="hallway14" onClick={character_movement}></div>
                            <div className="col lounge" id="lounge15" onClick={character_movement}></div>
                        </div>

                        <div className="row ms-2">
                            <div className="col hallway-vertical" id="hallway21" onClick={character_movement}></div>
                            <div className="col"></div>
